mapper.insert:

    select max(type_no)+1 from book_type;

insert into book_type(type_no,type_dsc,parent_type_no,show,del) values(
#{id},#{name},#{parentTypeNo},1,0)

mapper.update:
update book_type

    type_dsc=#{name},
    parent_type_no=#{parentTypeNo}

where type_no=#{id}

update book_type
set parent_type_no=#{parent.userObject.id}
where type_no in

    #{it.userObject.id}
